import React, { useState } from 'react';

const MAX_FILE_SIZE_MB = 5; // 5 MB limit

const ResearcherProfile = ({
  profile,
  index,
  updateProfile,
  removeProfile,
  isFirst,
  total,
}) => {
  const [inputMethod, setInputMethod] = useState('text'); // 'text' or 'pdf'

  const handleInputChange = (field, value) => {
    updateProfile(index, { ...profile, [field]: value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const fileSizeMB = file.size / (1024 * 1024); // Convert bytes to MB
      if (fileSizeMB > MAX_FILE_SIZE_MB) {
        alert(`File size exceeds ${MAX_FILE_SIZE_MB} MB limit. Please upload a smaller file.`);
        return;
      }
      updateProfile(index, {
        ...profile,
        pdfFile: file,
        pdfFileName: file.name, // Store the file name
        text: '',
      });
    }
  };

  const toggleInputMethod = (method) => {
    setInputMethod(method);
    if (method === 'text') {
      updateProfile(index, { ...profile, pdfFile: null, pdfFileName: null });
    } else {
      updateProfile(index, { ...profile, text: '' });
    }
  };

  return (
    <div className="researcher-profile">
      <div className="profile-header">
        <div className="profile-title">
          <h3>Researcher {index + 1}</h3>
        </div>
        {!isFirst && (
          <button
            onClick={() => removeProfile(index)}
            className="remove-profile-button"
          >
            Remove
          </button>
        )}
      </div>
      <div className="profile-content">
        <div className="profile-details">
          <div className="input-group">
            <label>Name *</label>
            <input
              type="text"
              className="input-field"
              value={profile.name}
              onChange={(e) => handleInputChange('name', e.target.value)}
              placeholder="Enter researcher's name"
            />
          </div>
          <div className="input-group">
            <label>Affiliation *</label>
            <input
              type="text"
              className="input-field"
              value={profile.affiliation}
              onChange={(e) => handleInputChange('affiliation', e.target.value)}
              placeholder="Enter affiliation"
            />
          </div>
          <div className="input-group">
            <label>Research Area *</label>
            <input
              type="text"
              className="input-field"
              value={profile.researchArea}
              onChange={(e) => handleInputChange('researchArea', e.target.value)}
              placeholder="Enter research area"
            />
          </div>
        </div>

        <div className="input-method-toggle">
          <button
            className={`toggle-option ${inputMethod === 'text' ? 'active' : ''}`}
            onClick={() => toggleInputMethod('text')}
          >
            Text Input
          </button>
          <button
            className={`toggle-option ${inputMethod === 'pdf' ? 'active' : ''}`}
            onClick={() => toggleInputMethod('pdf')}
          >
            PDF Upload
          </button>
        </div>

        {inputMethod === 'text' ? (
          <div className="text-input-section">
            <label>Research Description *</label>
            <textarea
              className="text-area"
              value={profile.text}
              onChange={(e) => handleInputChange('text', e.target.value)}
              placeholder="Enter research description"
            />
          </div>
        ) : (
          <div className="pdf-upload-section">
            <label>Upload PDF *</label>
            <input
              type="file"
              accept="application/pdf"
              onChange={handleFileChange}
            />
            {profile.pdfFileName && !profile.pdfFile && (
              <p className="pdf-filename">
                Previously selected: {profile.pdfFileName} (please re-upload after page reload)
              </p>
            )}
            {profile.pdfFile && (
              <p className="pdf-filename">Selected: {profile.pdfFile.name}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default ResearcherProfile;
