import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import useCreateTeams from '../../hooks/useCreateTeams';
import useRoom from '../../hooks/useRoom';
import './ProfilePage.css';

const ProfilePage = () => {
  const navigate = useNavigate();
  const { roomId } = useParams();
  const { fetchExtractedKeywords } = useRoom();
  const { createTeams, isCreatingTeams } = useCreateTeams();

  const [profiles, setProfiles] = useState([]);

  useEffect(() => {
    const loadKeywords = async () => {
      if (roomId) {
        const keywords = await fetchExtractedKeywords(roomId);
        setProfiles(keywords || []);
      }
    };
    loadKeywords();
  }, [roomId, fetchExtractedKeywords]);

  const handleGenerateTeams = async () => {
    if (!roomId) return;
    await createTeams(roomId);
  };

  return (
    <div className="room-page">
      <div className="room-header">
        <div className="header-content">
          <div className="room-info">
            <h1>Research Profiles</h1>
            <p className="creator-info">
              {profiles.length} {profiles.length === 1 ? 'profile' : 'profiles'} extracted
            </p>
          </div>
          <div className="header-actions">
            <button 
              onClick={() => navigate('/')} 
              className="secondary-button"
            >
              ‚Üê Back to Home
            </button>
          </div>
        </div>
      </div>

      <div className="content-container">
        {profiles.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">
              <span role="img" aria-label="Research">üî¨</span>
            </div>
            <h3>No Profiles Available</h3>
            <p>Extract research profiles from the room page to get started</p>
            <button 
              onClick={() => navigate('/')} 
              className="primary-button"
            >
              Go to Homepage
            </button>
          </div>
        ) : (
          <>
            <div className="profile-grid">
              {profiles.map((profile, idx) => (
                <div className="profile-card" key={idx}>
                  <div className="card-header">
                    <h3>{profile.name}</h3>
                  </div>
                  <div className="card-body">
                    <h4 className="section-title">Research Topics:</h4>
                    {profile.research_topics?.length > 0 ? (
                      <div className="topics-container">
                        {profile.research_topics.map((topic, i) => (
                          <span key={i} className="topic-tag">{topic}</span>
                        ))}
                      </div>
                    ) : (
                      <p className="no-topics">No topics extracted</p>
                    )}
                  </div>
                </div>
              ))}
            </div>

            <div className="generate-teams-container">
              <button
                className="generate-teams-btn"
                onClick={handleGenerateTeams}
                disabled={isCreatingTeams || profiles.length === 0}
              >
                {isCreatingTeams ? (
                  <>
                    <span className="loading-spinner"></span> Creating Teams...
                  </>
                ) : (
                  'Generate Research Teams'
                )}
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
