    const addResearcherProfile = () => {
      if (profiles.length < MAX_RESEARCHERS) {
        setProfiles([...profiles, { ...initialResearcherProfile }]);
      }
    };

    // Remove a researcher profile
    const removeResearcherProfile = (index) => {
