import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import useProfileStore from '../store/profileStore';

const useExtractInterests = () => {
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const { setExtractedProfiles, setIsExtracting } = useProfileStore();

  const extractInterests = async (profiles) => {
    setIsLoading(true);
    setIsExtracting(true);

    const formData = new FormData();
    const apiData = profiles.map((profile) => ({
      name: profile.name,
      description: profile.text,
    }));

    formData.append('profiles', JSON.stringify(apiData));
    profiles.forEach((profile, index) => {
      if (profile.pdfFile) {
        formData.append(`pdf${index}`, profile.pdfFile);
      }
    });

    try {
      const response = await fetch('http://127.0.0.1:5000/nsf/extract_interests', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to fetch research interests');
      }

      const data = await response.json();
      setExtractedProfiles(data); // Store the extracted profiles in Zustand
      navigate('/profiles');
    } catch (error) {
      alert('Error extracting research interests. Please try again.');
      console.error(error);
    } finally {
      setIsLoading(false);
      setIsExtracting(false);
    }
  };

  return { extractInterests, isLoading };
};

export default useExtractInterests;
