import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css';

// Custom icons as SVG components
const CustomIcons = {
  FileUpload: () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4" />
      <polyline points="17 8 12 3 7 8" />
      <line x1="12" y1="3" x2="12" y2="15" />
    </svg>
  ),
  FileX: () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M14.5 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V7.5L14.5 2z" />
      <polyline points="14 2 14 8 20 8" />
      <line x1="9.5" y1="12.5" x2="14.5" y2="17.5" />
      <line x1="14.5" y1="12.5" x2="9.5" y2="17.5" />
    </svg>
  ),
  Link: () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M10 13a5 5 0 0 0 7.54.54l3-3a5 5 0 0 0-7.07-7.07l-1.72 1.71" />
      <path d="M14 11a5 5 0 0 0-7.54-.54l-3 3a5 5 0 0 0 7.07 7.07l1.71-1.71" />
    </svg>
  ),
  Users: () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" />
      <circle cx="9" cy="7" r="4" />
      <path d="M23 21v-2a4 4 0 0 0-3-3.87" />
      <path d="M16 3.13a4 4 0 0 1 0 7.75" />
    </svg>
  ),
  Lightbulb: () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <line x1="9" y1="18" x2="15" y2="18" />
      <line x1="10" y1="22" x2="14" y2="22" />
      <path d="M15.09 14c.18-.98.65-1.74 1.41-2.5A4.65 4.65 0 0 0 18 8 6 6 0 0 0 6 8c0 1 .23 2.23 1.5 3.5A4.61 4.61 0 0 1 8.91 14" />
    </svg>
  ),
  UserPlus: () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M16 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" />
      <circle cx="8.5" cy="7" r="4" />
      <line x1="20" y1="8" x2="20" y2="14" />
      <line x1="23" y1="11" x2="17" y2="11" />
    </svg>
  ),
  UserMinus: () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M16 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" />
      <circle cx="8.5" cy="7" r="4" />
      <line x1="23" y1="11" x2="17" y2="11" />
    </svg>
  ),
  ChevronDown: () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <polyline points="6 9 12 15 18 9" />
    </svg>
  ),
  ChevronUp: () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <polyline points="18 15 12 9 6 15" />
    </svg>
  )
};

// Default research areas
const researchAreas = [
  "Computer Science",
  "Environmental Science",
  "Materials Science",
  "Physics",
  "Biology",
  "Chemistry",
  "Engineering",
  "Mathematics",
  "Social Sciences",
  "Other"
];

// Initial researcher profile template
const initialResearcherProfile = {
  name: '',
  affiliation: '',
  researchArea: '',
  uploadType: 'pdf', // pdf, text, or url
  files: [],
  text: '',
  url: '',
  expanded: true
};

const ResearcherProfile = ({ 
  profile, 
  index, 
  updateProfile, 
  removeProfile, 
  isFirst, 
  total 
}) => {
  // Toggle expanded state for this profile
  const toggleExpanded = () => {
    updateProfile(index, { ...profile, expanded: !profile.expanded });
  };

  // Handle file selection
  const handleFileChange = (e) => {
    const newFiles = Array.from(e.target.files);
    updateProfile(index, { ...profile, files: [...profile.files, ...newFiles] });
  };

  // Remove a file from this profile
  const removeFile = (fileIndex) => {
    const updatedFiles = profile.files.filter((_, i) => i !== fileIndex);
    updateProfile(index, { ...profile, files: updatedFiles });
  };

  // Handle text input for the researcher's profile
  const handleTextChange = (e) => {
    updateProfile(index, { ...profile, text: e.target.value });
  };

  // Handle URL input for the researcher's profile
  const handleUrlChange = (e) => {
    updateProfile(index, { ...profile, url: e.target.value });
  };

  // Handle URL submission
  const handleUrlSubmit = (e) => {
    e.preventDefault();
    // In a real app, you'd validate and process the URL
    console.log(`URL submitted for researcher ${index}:`, profile.url);
  };

  return (
    <div className="researcher-profile">
      <div className="profile-header" onClick={toggleExpanded}>
        <div className="profile-title">
          <h3>
            {profile.name ? profile.name : `Researcher ${index + 1}`}
            {profile.affiliation ? ` - ${profile.affiliation}` : ''}
          </h3>
        </div>
        <button className="toggle-button">
          {profile.expanded ? <CustomIcons.ChevronUp /> : <CustomIcons.ChevronDown />}
        </button>
      </div>

      {profile.expanded && (
        <div className="profile-content">
          <div className="profile-details">
            <div className="input-group">
              <label htmlFor={`researcher-name-${index}`}>Researcher Name</label>
              <input
                id={`researcher-name-${index}`}
                type="text"
                placeholder="Enter researcher name"
                value={profile.name}
                onChange={(e) => updateProfile(index, { ...profile, name: e.target.value })}
                className="input-field"
              />
            </div>

            <div className="input-group">
              <label htmlFor={`researcher-affiliation-${index}`}>Affiliation/Institution</label>
              <input
                id={`researcher-affiliation-${index}`}
                type="text"
                placeholder="Enter affiliation or institution"
                value={profile.affiliation}
                onChange={(e) => updateProfile(index, { ...profile, affiliation: e.target.value })}
                className="input-field"
              />
            </div>

            <div className="input-group">
              <label htmlFor={`researcher-area-${index}`}>Research Area</label>
              <select
                id={`researcher-area-${index}`}
                value={profile.researchArea}
                onChange={(e) => updateProfile(index, { ...profile, researchArea: e.target.value })}
                className="select-field"
              >
                <option value="">-- Select Research Area --</option>
                {researchAreas.map((area) => (
                  <option key={area} value={area}>{area}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="upload-section">
            <div className="upload-method-tabs">
              <button
                className={`tab-button ${profile.uploadType === 'pdf' ? 'active' : ''}`}
                onClick={() => updateProfile(index, { ...profile, uploadType: 'pdf' })}
              >
                Upload PDF
              </button>
              <button
                className={`tab-button ${profile.uploadType === 'text' ? 'active' : ''}`}
                onClick={() => updateProfile(index, { ...profile, uploadType: 'text' })}
              >
                Enter Text
              </button>
              <button
                className={`tab-button ${profile.uploadType === 'url' ? 'active' : ''}`}
                onClick={() => updateProfile(index, { ...profile, uploadType: 'url' })}
              >
                Website Link
              </button>
            </div>

            <div className="upload-content">
              {profile.uploadType === 'pdf' && (
                <>
                  <div 
                    className="file-upload-area" 
                    onClick={() => document.getElementById(`fileInput-${index}`).click()}
                  >
                    <div className="upload-icon">
                      <CustomIcons.FileUpload />
                    </div>
                    <p className="upload-text">Drag and drop files or click to browse</p>
                    <p className="upload-subtext">Supports PDF files</p>
                    <input 
                      id={`fileInput-${index}`} 
                      type="file" 
                      multiple 
                      accept=".pdf" 
                      className="hidden-input" 
                      onChange={handleFileChange}
                    />
                  </div>

                  {profile.files.length > 0 && (
                    <div className="files-list-section">
                      <h4>Uploaded Files ({profile.files.length})</h4>
                      <div className="files-list">
                        {profile.files.map((file, fileIndex) => (
                          <div key={fileIndex} className="file-item">
                            <span className="file-name">{file.name}</span>
                            <button 
                              onClick={() => removeFile(fileIndex)}
                              className="remove-file-button">
                              <span className="remove-icon">
                                <CustomIcons.FileX />
                              </span>
                            </button>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </>
              )}

              {profile.uploadType === 'text' && (
                <div className="text-input-section">
                  <textarea
                    placeholder="Enter researcher profile information here..."
                    value={profile.text}
                    onChange={handleTextChange}
                    className="text-area"
                    rows="6"
                  ></textarea>
                </div>
              )}

              {profile.uploadType === 'url' && (
                <div className="url-input-section">
                  <form onSubmit={handleUrlSubmit} className="url-form">
                    <input 
                      type="url" 
                      placeholder="https://scholar.google.com/..." 
                      value={profile.url}
                      onChange={handleUrlChange}
                      className="url-input"
                    />
                    <button type="submit" className="add-url-button">
                      <span className="button-icon">
                        <CustomIcons.Link />
                      </span>
                      Add
                    </button>
                  </form>
                </div>
              )}
            </div>
          </div>

          {!isFirst && (
            <div className="remove-profile-container">
              <button 
                onClick={() => removeProfile(index)}
                className="remove-profile-button"
              >
                <span className="button-icon">
                  <CustomIcons.UserMinus />
                </span>
                Remove Researcher
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

const MAX_RESEARCHERS = 10;

const HomePage = () => {
  const [profiles, setProfiles] = useState([{ ...initialResearcherProfile }]);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Add a new researcher profile
  const addResearcherProfile = () => {
    if (profiles.length < MAX_RESEARCHERS) {
      setProfiles([...profiles, { ...initialResearcherProfile }]);
    }
  };

  // Remove a researcher profile
  const removeResearcherProfile = (index) => {
    setProfiles(profiles.filter((_, i) => i !== index));
  };

  // Update a specific profile
  const updateResearcherProfile = (index, updatedProfile) => {
    const updatedProfiles = [...profiles];
    updatedProfiles[index] = updatedProfile;
    setProfiles(updatedProfiles);
  };

  // Handle form submission
  const handleSubmit = () => {
    setIsLoading(true);

    // Validate form before submission
    const hasEmptyRequiredFields = profiles.some(profile => {
      if (!profile.name || !profile.affiliation || !profile.researchArea) return true;
      if (profile.uploadType === 'pdf' && profile.files.length === 0) return true;
      if (profile.uploadType === 'text' && !profile.text.trim()) return true;
      if (profile.uploadType === 'url' && !profile.url.trim()) return true;
      return false;
    });

    if (hasEmptyRequiredFields) {
      alert('Please fill in all required fields for each researcher profile');
      setIsLoading(false);
      return;
    }

    // Create file URLs before navigation
    const profilesWithFileUrls = profiles.map(profile => ({
      ...profile,
      files: profile.uploadType === 'pdf' 
        ? profile.files.map(file => ({
            name: file.name,
            url: URL.createObjectURL(file)
          }))
        : []
    }));

    // Navigate immediately with the prepared data
    navigate('/profiles', { state: { profiles: profilesWithFileUrls } });
    setIsLoading(false);
  };

  // Check if any researcher has valid data
  const hasValidData = profiles.some(profile => {
    return (
      (profile.uploadType === 'pdf' && profile.files.length > 0) ||
      (profile.uploadType === 'text' && profile.text.trim() !== '') ||
      (profile.uploadType === 'url' && profile.url.trim() !== '')
    );
  });

  return (
    <div className="homepage">
      <div className="container">
        <div className="hero-section">
          <h1>NSF Research Team Formation</h1>
          <p>
            Add researcher profiles to form interdisciplinary teams and generate 
            competitive NSF research proposals using AI-powered analysis.
          </p>
        </div>

        <div className="main-card">
          <div className="features-sidebar">
            <h2>How It Works</h2>

            <div className="feature-item">
              <div className="feature-icon blue">
                <CustomIcons.FileUpload />
              </div>
              <div className="feature-text">
                <h3>Add Researchers</h3>
                <p>Add up to 10 researcher profiles via PDF, text, or web links</p>
              </div>
            </div>

            <div className="feature-item">
              <div className="feature-icon indigo">
                <CustomIcons.Users />
              </div>
              <div className="feature-text">
                <h3>Team Formation</h3>
                <p>AI analyzes expertise and forms optimal research teams</p>
              </div>
            </div>

            <div className="feature-item">
              <div className="feature-icon purple">
                <CustomIcons.Lightbulb />
              </div>
              <div className="feature-text">
                <h3>Proposal Generation</h3>
                <p>Get AI-generated project ideas tailored to your team</p>
              </div>
            </div>
          </div>

          <div className="profiles-container">
            <div className="profiles-header">
              <h2>Researcher Profiles</h2>
              <p className="profiles-count">
                {profiles.length} of {MAX_RESEARCHERS} researchers added
              </p>
            </div>

            <div className="profiles-list">
              {profiles.map((profile, index) => (
                <ResearcherProfile 
                  key={index}
                  profile={profile}
                  index={index}
                  updateProfile={updateResearcherProfile}
                  removeProfile={removeResearcherProfile}
                  isFirst={index === 0}
                  total={profiles.length}
                />
              ))}
            </div>

            <div className="add-profile-container">
              <button 
                onClick={addResearcherProfile}
                disabled={profiles.length >= MAX_RESEARCHERS}
                className={`add-profile-button ${profiles.length >= MAX_RESEARCHERS ? 'disabled' : ''}`}
              >
                <span className="button-icon">
                  <CustomIcons.UserPlus />
                </span>
                Add Researcher
              </button>
              {profiles.length >= MAX_RESEARCHERS && (
                <p className="max-limit-message">Maximum of 10 researchers reached</p>
              )}
            </div>

            <div className="submit-container">
              <button 
                onClick={handleSubmit}
                disabled={!hasValidData || isLoading}
                className={`submit-button ${!hasValidData ? 'disabled' : ''}`}
              >
                {isLoading ? 'Processing...' : 'Generate Research Teams & Proposals'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
