import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import useRoom from '../../hooks/useRoom';
import './RoomPage.css';
import useProfileStore from '../../store/profileStore';
import useExtractInterests from '../../hooks/useExtractInterests';

const RoomPage = () => {
  const { roomId } = useParams();
  const { fetchRoomData, addProfile, extractTextFromPDF, removeProfile } = useRoom();
  const [room, setRoom] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [inputMethod, setInputMethod] = useState('description');
  const [formData, setFormData] = useState({ name: '', email: '', description: '', pdfFile: null });
  const { setProfiles } = useProfileStore();
  const { extractInterests, isLoading: extracting } = useExtractInterests();
  const userEmail = localStorage.getItem('userEmail');

  useEffect(() => {
    const loadRoom = async () => {
      setLoading(true);
      const data = await fetchRoomData(roomId);
      setRoom(data);
      setLoading(false);
    };
    loadRoom();
  }, [roomId]);

  const handleExtractResearch = async () => {
    const profileArray = Object.values(room?.profiles || {}).map((p) => ({
      name: p.name || '',
      affiliation: '',
      researchArea: '',
      text: p.description || '',
      pdfFile: null,
    }));

    const hasEmpty = profileArray.some((p) => !p.name || !p.text);

    if (hasEmpty) {
      alert('Each profile must have a name and description to proceed.');
      return;
    }

    setProfiles(profileArray);
    await extractInterests(profileArray);
  };

  const handleDeleteProfile = async (emailToRemove) => {
    const confirm = window.confirm('Are you sure you want to remove this profile?');
    if (!confirm) return;

    try {
      await removeProfile({
        roomID: roomId,
        email: emailToRemove,
        senderEmail: userEmail,
      });

      const updatedRoom = await fetchRoomData(roomId);
      setRoom(updatedRoom);
    } catch (error) {
      console.error('Failed to remove profile:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'pdfFile') {
      setFormData((prev) => ({ ...prev, pdfFile: files[0] }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleAddProfile = async (e) => {
    e.preventDefault();
    const { name, email, description, pdfFile } = formData;

    if (!name || !email || (inputMethod === 'description' && !description) || (inputMethod === 'pdf' && !pdfFile)) {
      alert('Please fill in all required fields');
      return;
    }

    try {
      let finalDescription = description;

      if (inputMethod === 'pdf' && pdfFile) {
        const extractedText = await extractTextFromPDF(pdfFile);
        if (!extractedText) {
          alert('Failed to extract text from PDF.');
          return;
        }
        finalDescription = extractedText;
      }

      await addProfile({ roomID: roomId, name, email, description: finalDescription });

      const updatedRoom = await fetchRoomData(roomId);
      setRoom(updatedRoom);
      setShowModal(false);
      setFormData({ name: '', email: '', description: '', pdfFile: null });
    } catch (err) {
      console.error('Error adding profile:', err);
      alert('Failed to add profile. Please try again.');
    }
  };

  if (loading) return (
    <div className="loading-container">
      <div className="loading-spinner"></div>
      <p>Loading room data...</p>
    </div>
  );

  if (!room) return (
    <div className="error-container">
      <h2>Room not found</h2>
      <p>The room you're looking for doesn't exist or may have been deleted.</p>
    </div>
  );

  const profiles = room.profiles || {};

  return (
    <div className="room-page">
      <div className="room-header">
        <div className="header-content">
          <div className="room-info">
            <h1>Room: <span>{room.RoomID}</span></h1>
            <p className="creator-info">Created by: <span>{room.creatorID}</span></p>
          </div>
          <div className="header-actions">
            <button 
              className="primary-button" 
              onClick={() => setShowModal(true)}
            >
              <span className="button-icon">+</span> Add Profile
            </button>
          </div>
        </div>
      </div>

      <div className="content-container">
        {Object.values(profiles).length > 0 ? (
          <>
            <div className="profile-grid">
              {Object.values(profiles).map((profile, idx) => (
                <div className="profile-card" key={idx}>
                  <div className="card-header">
                    <h3>{profile.name}</h3>
                    <span className="email-badge">{profile.email}</span>
                    {room.creatorID === userEmail && (
                      <button
                        className="delete-btn"
                        title="Delete Profile"
                        onClick={() => handleDeleteProfile(profile.email)}
                      >
                        üóëÔ∏è
                      </button>
                    )}
                  </div>
                  <div className="card-body">
                    <p className="profile-desc">{profile.description || 'No description provided'}</p>
                  </div>
                </div>
              ))}
            </div>

            <div className="action-container">
              <button
                className="generate-button"
                onClick={handleExtractResearch}
                disabled={extracting || Object.values(profiles).length === 0}
              >
                {extracting ? (
                  <>
                    <span className="loading-spinner"></span> Processing...
                  </>
                ) : (
                  'Extract Research Interests'
                )}
              </button>
            </div>
          </>
        ) : (
          <div className="empty-state">
            <div className="empty-icon">
              <span role="img" aria-label="Empty">üìÇ</span>
            </div>
            <h3>No profiles yet</h3>
            <p>Add the first profile to get started</p>
            <button 
              className="primary-button"
              onClick={() => setShowModal(true)}
            >
              Add Profile
            </button>
          </div>
        )}
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Add Researcher Profile</h2>
              <button 
                className="close-button"
                onClick={() => setShowModal(false)}
              >
                &times;
              </button>
            </div>

            <div className="input-method-toggle">
              <button
                type="button"
                className={`toggle-option ${inputMethod === 'description' ? 'active' : ''}`}
                onClick={() => setInputMethod('description')}
              >
                Text Description
              </button>
              <button
                type="button"
                className={`toggle-option ${inputMethod === 'pdf' ? 'active' : ''}`}
                onClick={() => setInputMethod('pdf')}
              >
                Upload PDF
              </button>
            </div>

            <form onSubmit={handleAddProfile} className="modal-form">
              <div className="form-group">
                <label>Name</label>
                <input 
                  name="name" 
                  type="text" 
                  placeholder="Researcher name" 
                  value={formData.name} 
                  onChange={handleInputChange} 
                  required 
                />
              </div>

              <div className="form-group">
                <label>Email</label>
                <input 
                  name="email" 
                  type="email" 
                  placeholder="researcher@example.com" 
                  value={formData.email} 
                  onChange={handleInputChange} 
                  required 
                />
              </div>

              {inputMethod === 'description' ? (
                <div className="form-group">
                  <label>Research Description</label>
                  <textarea 
                    name="description" 
                    placeholder="Enter research interests, publications, or background..." 
                    value={formData.description} 
                    onChange={handleInputChange} 
                    rows="5"
                  />
                </div>
              ) : (
                <div className="form-group">
                  <label>Upload PDF</label>
                  <div className="file-upload">
                    <input 
                      name="pdfFile" 
                      type="file" 
                      accept="application/pdf" 
                      onChange={handleInputChange} 
                    />
                    <span>{formData.pdfFile?.name || 'Choose a PDF file'}</span>
                  </div>
                </div>
              )}

              <div className="form-actions">
                <button type="button" className="secondary-button" onClick={() => setShowModal(false)}>
                  Cancel
                </button>
                <button type="submit" className="primary-button">
                  Add Profile
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoomPage;
