import { useCallback } from 'react';
import roomService from '../services/roomService';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

const useRoom = () => {
  const navigate = useNavigate();
  const fetchRoomData = useCallback(async (roomId) => {
    try {
      const data = await roomService.getRoomData(roomId);
      console.log('data at useRoom page:', data);
      return data;
    } catch (err) {
      console.error(err);
      toast.error('Failed to load room data.');
      return null;
    }
  }, []);

  const extractTextFromPDF = useCallback(async (pdfFile) => {
    try {
      const data = await roomService.extractTextFromPDF(pdfFile);
      return data.text; // expects { text: '...' }
    } catch (err) {
      console.error(err);
      toast.error('Failed to extract text from PDF.');
      return '';
    }
  }, []);

  const removeProfile = useCallback(async ({ roomID, email, senderEmail }) => {
    try {
      const result = await roomService.removeProfileFromRoom({ roomID, email, senderEmail });
      toast.success('Profile removed successfully.');
      return result;
    } catch (err) {
      console.error(err);
      toast.error('Failed to remove profile.');
      throw err;
    }
  }, []);

  const removeRoom = useCallback(async ({roomID, email}) => {
    try {
      const result = await roomService.removeRoomData({ roomID, email});
      toast.success('Room removed successfully.');
      return result;
    } catch (err) {
      console.error(err);
      toast.error('Failed to remove room.');
      throw err;
    }
  }, [])

  const addProfile = useCallback(async ({ roomID, name, email, description }) => {
    try {
      const result = await roomService.addProfileToRoom({ roomID, name, email, description });
      toast.success('Profile added successfully.');
      return result;
    } catch (err) {
      console.error(err);
      toast.error('Failed to add profile.');
      throw err;
    }
  }, []);

  const createRoom = useCallback(async (creatorID) => {
    try {
      const result = await roomService.createRoom(creatorID);
      toast.success('Room created successfully.');
      return result; // { message, RoomID }
    } catch (err) {
      console.error(err);
      toast.error('Failed to create room.');
      throw err;
    }
  }, []);

  const getRoomsByEmail = useCallback(async (email) => {
    try {
      const data = await roomService.getRoomsByEmail(email);
      return data.rooms || [];
    } catch (err) {
      console.error(err);
      toast.error('Failed to fetch rooms by email.');
      return [];
    }
  }, []);

  const extracted_keywords = useCallback(async (roomID) => {
    try {
      const data = await roomService.extractResearchKeywords(roomID);
      navigate(`/room/${roomID}/profiles`);
    } catch (err) {
      console.error(err);
      toast.error('Failed to extract keywords from room.');
      return [];
    }
  }, []);

  const fetchExtractedKeywords = useCallback(async (roomID) => {
    try {
      const data = await roomService.getExtractedKeywords(roomID);
      return data || [];
    } catch (error) {
      console.error('Error fetching extracted keywords:', error);
      toast.error('Failed to fetch extracted keywords.');
      return [];
    }
  }, []);

  const fetchTeams = useCallback(async (roomID) => {
    try {
      const data = await roomService.getTeamsByRoom(roomID);
      return data; // Should be an array of teams
    } catch (err) {
      console.error(err);
      toast.error('Failed to load teams data');
      return [];
    }
  }, []);

  return {
    fetchRoomData,
    fetchExtractedKeywords,
    fetchTeams,
    extractTextFromPDF,
    addProfile,
    removeProfile,
    createRoom,
    getRoomsByEmail,
    removeRoom,
    extracted_keywords,
  };
};

export default useRoom;
