import { create } from 'zustand';
import { persist } from 'zustand/middleware';

const useProfileStore = create(
  persist(
    (set) => ({
      profiles: [],
      extractedProfiles: [],
      teams: [],
      teamsWithProposals: [],
      isLoading: false,
      isCreatingTeams: false,
      isGeneratingProposals: false,

      addProfile: () =>
        set((state) => ({
          profiles: [
            ...state.profiles,
            {
              name: '',
              affiliation: '',
              researchArea: '',
              text: '',
              pdfFile: null,
            },
          ],
        })),

      removeProfile: (index) =>
        set((state) => ({
          profiles: state.profiles.filter((_, i) => i !== index),
        })),

      updateProfile: (index, updatedProfile) =>
        set((state) => {
          const newProfiles = [...state.profiles];
          newProfiles[index] = updatedProfile;
          return { profiles: newProfiles };
        }),

      setProfiles: (profiles) => set({ profiles }),

      setExtractedProfiles: (profiles) => set({ extractedProfiles: profiles }),
      setTeams: (teams) => set({ teams }),
      setTeamsWithProposals: (teams) => set({ teamsWithProposals: teams }),
      setIsLoading: (loading) => set({ isLoading: loading }),
      setIsCreatingTeams: (creating) => set({ isCreatingTeams: creating }),
      setIsGeneratingProposals: (generating) =>
        set({ isGeneratingProposals: generating }),
    }),
    {
      name: 'profile-storage',
      partialize: (state) => ({
        // Only persist the fields that can be serialized
        profiles: state.profiles.map((profile) => ({
          name: profile.name,
          affiliation: profile.affiliation,
          researchArea: profile.researchArea,
          text: profile.text,
          // Exclude pdfFile since File objects can't be serialized
        })),
        extractedProfiles: state.extractedProfiles,
        teams: state.teams,
        teamsWithProposals: state.teamsWithProposals,
        isLoading: state.isLoading,
        isCreatingTeams: state.isCreatingTeams,
        isGeneratingProposals: state.isGeneratingProposals,
      }),
      storage: {
        getItem: (name) => {
          const value = localStorage.getItem(name);
          return value ? JSON.parse(value) : null;
        },
        setItem: (name, value) => {
          localStorage.setItem(name, JSON.stringify(value));
        },
        removeItem: (name) => {
          localStorage.removeItem(name);
        },
      },
    }
  )
);

export default useProfileStore;
