import { useCallback } from 'react';
import roomService from '../services/roomService';
import { toast } from 'react-toastify';

const useRoom = () => {
  const fetchRoomData = useCallback(async (roomId) => {
    try {
      const data = await roomService.getRoomData(roomId);
      return data;
    } catch (err) {
      console.error(err);
      toast.error('Failed to load room data.');
      return null;
    }
  }, []);

  const addProfile = useCallback(async ({ roomID, name, email, description, pdfFile }) => {
    try {
      const response = await roomService.addProfileToRoom({
        roomID,
        name,
        email,
        description,
        pdfFile,
      });
      toast.success('Profile added successfully!');
      return response;
    } catch (err) {
      console.error(err);
      toast.error('Failed to add profile.');
      throw err;
    }
  }, []);

  return { fetchRoomData, addProfile };
};

export default useRoom;
