import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './TeamPage.css'; // Updated styles

const TeamPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const teams = location.state?.teams || [];
  const [loading, setLoading] = useState(false);

  const handleGenerateProposals = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/generate-proposals', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(teams)
      });
      if (!response.ok) {
        throw new Error('Failed to generate proposals');
      }
      const data = await response.json();
      // Navigate to the new page and pass the updated teams data (with proposals) via state
      navigate('/teamswithproposal', { state: { teams: data } });
    } catch (error) {
      console.error(error);
      alert('Error generating proposals. Please try again later.');
    }
    setLoading(false);
  };

  return (
    <div className="team-page">
      <h1>Research Teams</h1>
      {teams.length === 0 ? (
        <p className="no-teams">No teams formed.</p>
      ) : (
        <div className="teams-list">
          {teams.map((team) => (
            <div key={team.team_id} className="team-card">
              <h2>Team {team.team_id} (Size: {team.team_size})</h2>
              <h3>Team Research Areas:</h3>
              <div className="team-research-areas">
                {team.team_research_areas.map((area, idx) => (
                  <span key={idx} className="research-area-tag">{area}</span>
                ))}
              </div>
              <h3>Members:</h3>
              <table>
                <thead>
                  <tr>
                    <th>Member Name</th>
                    <th>Fields</th>
                  </tr>
                </thead>
                <tbody>
                  {team.members.map((member) => (
                    <tr key={member}>
                      <td>{member}</td>
                      <td>{team.member_fields[member].join(', ')}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      )}
      <div className="buttons-container">
        <button onClick={() => navigate('/')} className="back-button">
          Back to Home
        </button>
        <button 
          onClick={handleGenerateProposals} 
          className="generate-button" 
          disabled={loading}
        >
          {loading ? 'Generating Proposals...' : 'Generate Proposals'}
        </button>
      </div>
    </div>
  );
};

export default TeamPage;
