import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './IntroPage.css';
import useRoom from '../../hooks/useRoom';
import { toast } from 'react-toastify';

const IntroPage = () => {
  const navigate = useNavigate();
  const [roomID, setRoomID] = useState('');
  const [error, setError] = useState('');
  const [userRooms, setUserRooms] = useState([]);
  const { createRoom, getRoomsByEmail } = useRoom();

  const userEmail = localStorage.getItem('userEmail');

  useEffect(() => {
    const fetchRooms = async () => {
      if (!userEmail) {
        toast.error('User not logged in.');
        return;
      }
      try {
        const rooms = await getRoomsByEmail(userEmail);
        setUserRooms(rooms);
      } catch (err) {
        toast.error('Could not fetch rooms.');
      }
    };

    fetchRooms();
  }, [userEmail]);

  const handleJoinRoom = () => {
    const trimmedID = roomID.trim();
    if (!trimmedID) {
      setError('Room ID cannot be empty.');
      return;
    }
    setError('');
    navigate(`/room/${trimmedID}`);
  };

  const handleCreateRoom = async () => {
    try {
      if (!userEmail) {
        toast.error('User not logged in.');
        return;
      }

      const res = await createRoom(userEmail);
      const newRoomId = res.RoomID;
      navigate(`/room/${newRoomId}`);
    } catch (err) {
      toast.error('Failed to create room. Please try again.');
    }
  };

  return (
    <div className="intro-page">
      <div className="container">
        <div className="intro-hero">
          <h1>Welcome to ResearchConnect</h1>
          <p>
            ResearchConnect helps you build interdisciplinary research teams and generate impactful project proposals using AI.
            Collaborate seamlessly with peers by joining or creating a research room.
          </p>
        </div>

        <div className="intro-actions">
          <button className="intro-button create-button" onClick={handleCreateRoom}>
            🚀 Create a New Room
          </button>

          <div className="join-room-form">
            <input
              className="input-field"
              type="text"
              placeholder="Enter Room ID"
              value={roomID}
              onChange={(e) => {
                setRoomID(e.target.value);
                setError('');
              }}
              aria-label="Room ID"
            />
            <button className="intro-button join-button" onClick={handleJoinRoom}>
              🔑 Join Room
            </button>
          </div>

          {error && <p className="error-message">{error}</p>}
        </div>

        {userRooms.length > 0 && (
          <div className="room-list">
            <h2>Your Rooms</h2>
            <div className="room-cards">
              {userRooms.map((room, index) => (
                <div
                  key={room.RoomID}
                  className="room-card"
                  onClick={() => navigate(`/room/${room.RoomID}`)}
                >
                  <h3>Room {index + 1}</h3>
                  <p><strong>Room ID:</strong> {room.RoomID}</p>
                  <p><strong>Profiles:</strong> {Object.keys(room.profiles || {}).length}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default IntroPage;
