import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './IntroPage.css';
import useRoom from '../../hooks/useRoom';
import { toast } from 'react-toastify';

const IntroPage = () => {
  const navigate = useNavigate();
  const [roomID, setRoomID] = useState('');
  const [error, setError] = useState('');
  const [userRooms, setUserRooms] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const { createRoom, getRoomsByEmail, removeRoom } = useRoom();

  const userEmail = localStorage.getItem('userEmail');

  const fetchRooms = async () => {
    if (!userEmail) {
      toast.error('Please log in to view your rooms');
      return;
    }
    setIsLoading(true);
    try {
      const rooms = await getRoomsByEmail(userEmail);
      setUserRooms(rooms);
    } catch (err) {
      toast.error('Could not fetch rooms');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchRooms();
  }, [userEmail]);

  const handleJoinRoom = () => {
    const trimmedID = roomID.trim();
    if (!trimmedID) {
      setError('Please enter a Room ID');
      return;
    }
    setError('');
    navigate(`/room/${trimmedID}`);
  };

  const handleCreateRoom = async () => {
    try {
      if (!userEmail) {
        toast.error('Please log in to create a room');
        return;
      }

      setIsLoading(true);
      const res = await createRoom(userEmail);
      const newRoomId = res.RoomID;
      navigate(`/room/${newRoomId}`);
    } catch (err) {
      toast.error('Failed to create room. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteRoom = async (roomID) => {
    if (!window.confirm('Are you sure you want to delete this room?')) return;
    try {
      await removeRoom({ roomID, email: userEmail });
      fetchRooms();
    } catch (err) {
      toast.error('Failed to delete room');
    }
  };

  return (
    <div className="intro-page">
      <div className="container">
        <div className="intro-hero">
          <div className="hero-content">
            <h1>Welcome to <span>ResearchConnect</span></h1>
            <p className="hero-subtitle">
              Build interdisciplinary research teams and generate impactful project proposals with AI-powered collaboration.
            </p>
          </div>
          <div className="hero-image">
            <span role="img" aria-label="Research collaboration" className="emoji-icon">👥</span>
          </div>
        </div>

        <div className="intro-actions">
          <div className="action-card create-room">
            <div className="action-content">
              <h3>Start a new collaboration</h3>
              <p>Create a dedicated space for your research team</p>
              <button 
                className="primary-button" 
                onClick={handleCreateRoom}
                disabled={isLoading}
              >
                {isLoading ? (
                  <span className="button-loader"></span>
                ) : (
                  <>
                    <span role="img" aria-label="Rocket" className="emoji-icon">🚀</span>
                    Create New Room
                  </>
                )}
              </button>
            </div>
          </div>

          <div className="action-card join-room">
            <div className="action-content">
              <h3>Join existing room</h3>
              <p>Enter the Room ID provided by your collaborator</p>
              <div className="join-form">
                <input
                  type="text"
                  placeholder="Enter Room ID"
                  value={roomID}
                  onChange={(e) => {
                    setRoomID(e.target.value);
                    setError('');
                  }}
                  aria-label="Room ID"
                  className="text-input"
                />
                <button 
                  className="secondary-button"
                  onClick={handleJoinRoom}
                  disabled={isLoading}
                >
                  <span role="img" aria-label="Key" className="emoji-icon">🔑</span>
                  Join Room
                </button>
              </div>
              {error && <p className="error-message">{error}</p>}
            </div>
          </div>
        </div>

        {isLoading ? (
          <div className="loading-rooms">
            <div className="spinner"></div>
            <p>Loading your rooms...</p>
          </div>
        ) : userRooms.length > 0 && (
          <div className="room-list-section">
            <div className="section-header">
              <h2>Your Recent Rooms</h2>
              {userRooms.length > 3 && (
                <button className="text-button">View all</button>
              )}
            </div>
            <div className="room-grid">
              {userRooms.slice(0, 3).map((room) => (
                <div key={room.RoomID} className="room-card">
                  <div className="card-content" onClick={() => navigate(`/room/${room.RoomID}`)}>
                    <h3>{room.name || `Room ${room.RoomID.slice(0, 6)}`}</h3>
                    <div className="room-stats">
                      <span className="stat">
                        <strong>{Object.keys(room.profiles || {}).length}</strong> members
                      </span>
                      <span className="stat">
                        <strong>{room.lastActive || 'New'}</strong>
                      </span>
                    </div>
                  </div>
                  <div className="delete-room" onClick={() => handleDeleteRoom(room.RoomID)}>
                    <span role="img" aria-label="Delete" className="emoji-icon">🗑️</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default IntroPage;
