// src/components/TeamProposalCard.js
import React from 'react';
import './TeamProposalCard.css'; // Optional: separate styling

const TeamProposalCard = ({ team }) => {
  return (
    <div className="team-proposal-container">
      {/* Left Panel - Team Info */}
      <div className="team-info-panel">
        <div className="team-header">
          <h2>Team {team.team_id}</h2>
          <span className="team-meta">
            {team.team_size} member{team.team_size !== 1 ? 's' : ''}
          </span>
        </div>

        <div className="team-section">
          <h3 className="section-title">
            <span className="section-icon">🔍</span> Research Focus
          </h3>
          <div className="tags-container">
            {team.team_research_areas.map((area, idx) => (
              <span key={idx} className="research-tag">{area}</span>
            ))}
          </div>
        </div>

        <div className="team-section">
          <h3 className="section-title">
            <span className="section-icon">👥</span> Members
          </h3>
          <div className="members-list">
            {team.members.map((member) => (
              <div key={member} className="member-item">
                <div className="member-name">{member}</div>
                <div className="member-expertise">
                  {team.member_fields[member].slice(0, 5).map((field, idx) => (
                    <span key={idx} className="expertise-tag">{field}</span>
                  ))}
                  {team.member_fields[member].length > 5 && (
                    <span className="expertise-tag">
                      +{team.member_fields[member].length - 5} more
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Right Panel - Proposals */}
      <div className="proposals-panel">
        <div className="proposals-header">
          <h3>
            <span className="section-icon">📑</span> Project Proposals
          </h3>
          <span className="proposals-count">
            {team.project_proposals?.length || 0} proposal{team.project_proposals?.length !== 1 ? 's' : ''}
          </span>
        </div>

        <div className="proposals-list">
          {team.project_proposals?.length > 0 ? (
            team.project_proposals.map((proposal, idx) => (
              <div key={idx} className="proposal-card">
                <div className="proposal-header">
                  <h4>Proposal #{idx + 1}</h4>
                </div>
                <div className="proposal-content">
                  <p className="proposal-text">{proposal}</p>
                </div>
              </div>
            ))
          ) : (
            <div className="no-proposals">
              No proposals generated for this team
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TeamProposalCard;
