import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import useRoom from '../../hooks/useRoom';
import './RoomPage.css';
import useProfileStore from '../../store/profileStore'; // ✅ import Zustand store
import useExtractInterests from '../../hooks/useExtractInterests'; // ✅ import extract hook

const RoomPage = () => {
  const { roomId } = useParams();
  const { fetchRoomData, addProfile, extractTextFromPDF } = useRoom();
  const [room, setRoom] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [inputMethod, setInputMethod] = useState('description'); // 'description' or 'pdf'
  const [formData, setFormData] = useState({ name: '', email: '', description: '', pdfFile: null });
  const { setProfiles } = useProfileStore();
  const { extractInterests, isLoading: extracting } = useExtractInterests();

  useEffect(() => {
    const loadRoom = async () => {
      setLoading(true);
      const data = await fetchRoomData(roomId);
      setRoom(data);
      setLoading(false);
    };
    loadRoom();
  }, [roomId]);

  const handleExtractResearch = async () => {
    const profileArray = Object.values(room?.profiles || {}).map((p) => ({
      name: p.name || '',
      affiliation: '', // default placeholder
      researchArea: '', // default placeholder
      text: p.description || '',
      pdfFile: null,
    }));

    const hasEmpty = profileArray.some(
      (p) => !p.name || !p.text
    );

    if (hasEmpty) {
      alert('Each profile must have a name and description to proceed.');
      return;
    }

    setProfiles(profileArray); // ✅ Store into Zustand
    await extractInterests(profileArray); // ✅ Trigger extraction
  };


  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'pdfFile') {
      setFormData((prev) => ({ ...prev, pdfFile: files[0] }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleAddProfile = async (e) => {
    e.preventDefault();
    const { name, email, description, pdfFile } = formData;

    if (!name || !email || (inputMethod === 'description' && !description) || (inputMethod === 'pdf' && !pdfFile)) {
      alert('Please fill in name, email, and either a description or a PDF file.');
      return;
    }

    try {
      let finalDescription = description;

      if (inputMethod === 'pdf' && pdfFile) {
        const extractedText = await extractTextFromPDF(pdfFile);
        if (!extractedText) {
          alert('Failed to extract text from PDF.');
          return;
        }
        finalDescription = extractedText;
      }

      await addProfile({ roomID: roomId, name, email, description: finalDescription });

      const updatedRoom = await fetchRoomData(roomId);
      setRoom(updatedRoom);
      setShowModal(false);
      setFormData({ name: '', email: '', description: '', pdfFile: null });
    } catch (err) {
      console.error('Error adding profile:', err);
      alert('Failed to add profile. Please try again.');
    }
  };

  if (loading) return <div className="room-loading">Loading room data...</div>;
  if (!room) return <div className="room-error">Room not found</div>;

  const profiles = room.profiles || {};

  return (
    <div className="room-page">
      <div className="room-header">
        <h1>Room ID: {room.RoomID}</h1>
        <p>Created by: {room.creatorID}</p>
        <button className="add-profile-btn" onClick={() => setShowModal(true)}>+ Add Profile</button>
      </div>

      <div className="profile-grid">
        {Object.values(profiles).map((profile, idx) => (
          <div className="profile-card" key={idx}>
            <h3>{profile.name}</h3>
            <p className="email">{profile.email}</p>
            <p className="desc">{profile.description}</p>
          </div>
        ))}
      </div>

      <div className="generate-teams-container">
        <button
              className="generate-teams-btn"
              onClick={handleExtractResearch}
              disabled={extracting}
            >
          {extracting ? 'Extracting...' : 'Extract Research'}
        </button>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add Researcher Profile</h2>

            {/* Input method toggle */}
            <div className="input-method-toggle">
              <button
                type="button"
                className={`toggle-option ${inputMethod === 'description' ? 'active' : ''}`}
                onClick={() => setInputMethod('description')}
              >
                Type Description
              </button>
              <button
                type="button"
                className={`toggle-option ${inputMethod === 'pdf' ? 'active' : ''}`}
                onClick={() => setInputMethod('pdf')}
              >
                Upload PDF
              </button>
            </div>

            <form onSubmit={handleAddProfile} className="modal-form">
              <input name="name" type="text" placeholder="Name" value={formData.name} onChange={handleInputChange} required />
              <input name="email" type="email" placeholder="Email" value={formData.email} onChange={handleInputChange} required />

              {inputMethod === 'description' ? (
                <textarea name="description" placeholder="Description" value={formData.description} onChange={handleInputChange} />
              ) : (
                <input name="pdfFile" type="file" accept="application/pdf" onChange={handleInputChange} />
              )}

              <div className="modal-actions">
                <button type="submit">Submit</button>
                <button type="button" onClick={() => setShowModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoomPage;
