import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import './ProfilesPage.css';

const ProfilesPage = () => {
    const location = useLocation();
    const profiles = location.state?.profiles || [];
    const [viewMode, setViewMode] = useState('grid'); // 'grid' or 'list'

    // Cleanup object URLs
    useEffect(() => {
        return () => {
            location.state?.profiles?.forEach(profile => {
                if (profile.uploadType === 'pdf') {
                    profile.files.forEach(file => URL.revokeObjectURL(file.url));
                }
            });
        };
    }, []);

    return (
        <div className="profiles-page">
            <div className="profiles-header">
                <h1>Researcher Profiles</h1>
                <div className="view-toggle">
                    <button 
                        className={`view-button ${viewMode === 'grid' ? 'active' : ''}`}
                        onClick={() => setViewMode('grid')}
                    >
                        Grid View
                    </button>
                    <button 
                        className={`view-button ${viewMode === 'list' ? 'active' : ''}`}
                        onClick={() => setViewMode('list')}
                    >
                        List View
                    </button>
                </div>
            </div>

            <div className={`profiles-container ${viewMode}-view`}>
                {profiles.map((profile, index) => (
                    <div key={index} className="profile-card">
                        {<div key={index} className="profile-card">
                          <div className="card-header">
                            <h2>{profile.name}</h2>
                            <p className="affiliation">{profile.affiliation}</p>
                            <div className="research-area">
                              <span>{profile.researchArea}</span>
                            </div>
                          </div>

                          <div className="card-content">
                          {profile.uploadType === 'pdf' && (
                          <div className="upload-content">
                              <h4>Uploaded Documents ({profile.files.length})</h4>
                              <div className="pdf-list">
                              {profile.files.map((file, fileIndex) => (
                                  <a
                                  key={fileIndex}
                                  href={file.url}  // Use the pre-generated URL
                                  download={file.name}
                                  className="pdf-item"
                                  >
                                  ðŸ“„ {file.name}
                                  </a>
                              ))}
                              </div>
                          </div>
                          )}

                            {profile.uploadType === 'url' && (
                              <div className="upload-content">
                                <h4>Research Links</h4>
                                <a
                                  href={profile.url}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="research-link"
                                >
                                  {profile.url}
                                </a>
                              </div>
                            )}

                            {profile.uploadType === 'text' && (
                              <div className="upload-content">
                                <h4>Research Summary</h4>
                                <div className="text-content">{profile.text}</div>
                              </div>
                            )}
                          </div>
                        </div>}
                    </div>
                ))}
            </div>

            <div className="create-teams-footer">
                <Link to="/teams" className="create-teams-button">
                    Create Teams
                </Link>
            </div>
        </div>
    );
};
