import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import useRoom from '../../hooks/useRoom';
import './RoomPage.css';

const RoomPage = () => {
  const { roomId } = useParams();
  const { fetchRoomData, addProfile, extractTextFromPDF } = useRoom();
  const [room, setRoom] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', description: '', pdfFile: null });

  useEffect(() => {
    const loadRoom = async () => {
      setLoading(true);
      const data = await fetchRoomData(roomId);
      setRoom(data);
      setLoading(false);
    };
    loadRoom();
  }, [roomId, fetchRoomData]);

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;

    // If PDF is uploaded, clear description
    if (name === 'pdfFile') {
      setFormData((prev) => ({
        ...prev,
        pdfFile: files[0],
        description: '', // reset description
      }));
    } else if (name === 'description') {
      setFormData((prev) => ({
        ...prev,
        description: value,
        pdfFile: null, // reset file
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleAddProfile = async (e) => {
    e.preventDefault();

    const { name, email, description, pdfFile } = formData;

    if (!name || !email) {
      alert('Name and email are required.');
      return;
    }

    if (!description && !pdfFile) {
      alert('Provide either a description or a PDF.');
      return;
    }

    try {
      let finalDescription = description;

      if (pdfFile) {
        // Extract text from PDF
        const extractedText = await extractTextFromPDF(pdfFile);
        finalDescription = extractedText;
      }

      await addProfile({
        name,
        email,
        description: finalDescription,
        roomID: roomId,
        pdfFile: pdfFile || null,
      });

      const updatedRoom = await fetchRoomData(roomId);
      setRoom(updatedRoom);

      // Reset form and modal
      setFormData({ name: '', email: '', description: '', pdfFile: null });
      setShowModal(false);
    } catch (err) {
      console.error('Error adding profile:', err);
    }
  };

  if (loading) return <div className="room-loading">Loading room data...</div>;
  if (!room) return <div className="room-error">Room not found</div>;

  const profiles = room.profiles || {};

  return (
    <div className="room-page">
      <div className="room-header">
        <h1>Room ID: {room.RoomID}</h1>
        <p>Created by: {room.creatorID}</p>
        <button className="add-profile-btn" onClick={() => setShowModal(true)}>
          + Add Profile
        </button>
      </div>

      <div className="profile-grid">
        {Object.values(profiles).map((profile, idx) => (
          <div className="profile-card" key={idx}>
            <h3>{profile.name}</h3>
            <p className="email">{profile.email}</p>
            <p className="desc">{profile.description}</p>
          </div>
        ))}
      </div>

      <div className="generate-teams-container">
        <button className="generate-teams-btn">Generate Teams</button>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add Researcher Profile</h2>
            <form onSubmit={handleAddProfile} className="modal-form">
              <input
                name="name"
                type="text"
                placeholder="Name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
              <input
                name="email"
                type="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              <textarea
                name="description"
                placeholder="Description (or upload PDF)"
                value={formData.description}
                onChange={handleInputChange}
                disabled={formData.pdfFile !== null}
              />
              <input
                name="pdfFile"
                type="file"
                accept="application/pdf"
                onChange={handleInputChange}
                disabled={formData.description.length > 0}
              />
              <div className="modal-actions">
                <button type="submit">Submit</button>
                <button type="button" onClick={() => setShowModal(false)}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoomPage;
