import { useCallback } from 'react';
import roomService from '../services/roomService';
import { toast } from 'react-toastify';

const useRoom = () => {
  const fetchRoomData = useCallback(async (roomId) => {
    try {
      const data = await roomService.getRoomData(roomId);
      console.log('data at useRoom page:', data);
      return data;
    } catch (err) {
      console.error(err);
      toast.error('Failed to load room data.');
      return null;
    }
  }, []);

  const extractTextFromPDF = useCallback(async (pdfFile) => {
    try {
      const data = await roomService.extractTextFromPDF(pdfFile);
      return data.text; // { text: '...' }
    } catch (err) {
      console.error(err);
      toast.error('Failed to extract text from PDF.');
      return '';
    }
  }, []);

  return {
    fetchRoomData,
    extractTextFromPDF,
  };
};

export default useRoom;
